import { useEffect, useState } from "react";

export default function Posts() {
    const [posts, setPosts] = useState([]);
    const [message, setMessage] = useState('');

    const deletePost = async (postId) => {
        const token = localStorage.getItem('token');

        try {
            const response = await fetch(`/posts/delete/${postId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            const result = await response.json();
            if (response.ok) {
                setMessage(result.message);
                // Оновлення списку постів
                setPosts(posts.filter(post => post._id !== postId));
            } else {
                setMessage(result.message || 'Помилка при видаленні');
            }
        } catch (error) {
            console.error('Помилка при видаленні поста:', error);
            setMessage('Сталася помилка');
        }
    };

    return (
        <div>
            <h1>Пости</h1>
            {message && <p>{message}</p>}
            <ul>
                {posts.map(post => (
                    <li key={post._id}>
                        {post.title}
                        <button onClick={() => deletePost(post._id)}>Видалити</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
