import { useState, useEffect } from "react";

export default function GetUserGames() {
    const [data, setData] = useState(null);
    const [trophy, setTrophy] = useState({});
    const user_id = "76561198951455714";

    useEffect(() => {
        // Завантажуємо ігри користувача
        fetch('/steam/userGames')
            .then(res => res.json())
            .then(res => setData(res.message));
    }, []);

    useEffect(() => {
        // Завантажуємо досягнення для кожної гри
        if (data) {
            data.forEach(game => {
                if (!trophy[game.game.id]) {
                    getach(game.game.id);
                }
            });
        }
    }, [data]);

    const getach = async (id) => {
        try {
            const response = await fetch(`/steam/userGameAchievements/${user_id}/${id}`);
            const result = await response.json();
            setTrophy(prevTrophies => ({
                ...prevTrophies,
                [id]: result.message, // Додаємо досягнення у стан
            }));
        } catch (error) {
            console.error(`Помилка отримання досягнень для гри ${id}:`, error);
        }
    };

    return (
        <div className="content">
            <h3>{user_id}</h3>
            <table className="steamGames">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Іконка</th>
                        <th>Назва</th>
                        <th>Досягнення</th>
                        <th>Час у грі</th>
                    </tr>
                </thead>
                <tbody>
                    {!data ? (
                        <tr>
                            <td colSpan="5">Завантаження...</td>
                        </tr>
                    ) : (
                        data.map((game, index) => {
                            const link = `https://shared.cloudflare.steamstatic.com/store_item_assets/steam/apps/${game.game.id}/${game.game.capsuleFilename}`;
                            return (
                                <tr className="userGame" key={index}>
                                    <td>{index + 1}</td>
                                    <td>
                                        <img src={link} alt={game.game.name} style={{ width: '50px' }} />
                                    </td>
                                    <td>{game.game.name}</td>
                                    <td>
                                    {trophy[game.game.id] && (
    <div>
        Завершено: {trophy[game.game.id].completed.unlockedCount} / {trophy[game.game.id].fullAmount.achvAmount}
    </div>
)}
                                    </td>
                                    <td>{Math.floor(game.minutes / 60)} г.</td>
                                </tr>
                            );
                        })
                    )}
                </tbody>
            </table>
        </div>
    );
}
