import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function GetUserAchievement() {
    const navigate = useNavigate();
    const [steamId, setSteamId] = useState('');
    const [gameId, setGameId] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!steamId || !gameId) {
            setError('Будь ласка, введіть ID');
            return;
        }

        try {
            const response = await fetch(`/posts/getOne/${inputOne}`, {
                method: 'GET',
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error('Помилка при отриманні даних');
            }

            const postData = await response.json();
            setData(postData);

            // Переходити на нову сторінку з використанням ID
            navigate(`/posts/${inputOne}`);
        } catch (err) {
            setError(err.message);
        }
    }

    return (
        <div className="content">
            <h1>Знайти пост</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Введіть SteamID"
                    value={steamId}
                    onChange={(event) => setSteamId(event.target.value)}
                />
                <input
                    type="text"
                    placeholder="Введіть ID гри"
                    value={gameId}
                    onChange={(event) => setGameId(event.target.value)}
                />
                <button type="submit">Відправити</button>
            </form>
        </div>
    )
}
