import { useState } from "react";
import axios from "axios";
export default function CreatePost() {

    const [file, setFile] = useState(null);
    const [message, setMessage] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem('token');

        const formData = new FormData();
        formData.append('image', file);

        try {
            const response = await fetch('http://localhost:3001/posts/create', {
                method: 'POST',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
                body: formData,
            });

            const data = await response.json();
            if (response.ok) {
                setMessage('Файл успішно завантажено');
            } else {
                setMessage(data.message || 'Щось пішло не так');
            }
        } catch (error) {
            console.error('Помилка при завантаженні файлу:', error);
            setMessage('Сталася помилка при завантаженні файлу');
        }
    };

    return (
        <div className="content">
            <form onSubmit={handleSubmit}>
                <div>
                    <h2>Дані посту:</h2>
                    <div className='post'>
                        <input type="file" onChange={handleFileChange} />
                        <div>{data._id}</div>
                        <h3>{data.title}</h3>
                        <textarea>{data.text}</textarea>
                        <br />
                        <span><b>views:</b>{data.viewsCount}&nbsp;&nbsp;</span>
                        <span>{data.createdAt}</span>
                        <br />
                        <div><b>Tags:</b></div>
                        {
                            data.tags.map((tag, index) => {
                                return (<span>
                                    &nbsp;
                                    {tag}
                                </span>)
                            })
                        }
                        <br />
                    </div>
                </div>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}
