import { useEffect, useState } from 'react'

export default function Me() {
    const [data, setData] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token'); // Отримуємо токен з localStorage

        if (token) {
            fetch('/auth/me', {
                method: 'GET',
                headers: {
                    Authorization: `Bearer ${token}`, // Передаємо токен в заголовках
                    'Content-Type': 'application/json',
                },
            })
                .then((res) => {
                    if (!res.ok) {
                        throw new Error('Failed to fetch user data');
                    }
                    return res.json();
                })
                .then((res) => setData(res)) // Зберігаємо отримані дані
                .catch((err) => {
                    console.error('Error fetching data:', err);
                    setData('Помилка при отриманні даних');
                });
        } else {
            setData('Токен відсутній');
        }
    }, []);
    return (
        <>{data}
        {console.log(localStorage)}
        </>
    )
}
