import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function GetOnePost() {
    const [data, setData] = useState(null);
    const [inputOne, setInputOne] = useState('');
    const [error, setError] = useState(null);
    const navigate = useNavigate(); 

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!inputOne) {
            setError('Будь ласка, введіть ID');
            return;
        }

        if (token) {
            try {
                const response = await fetch(`/posts/getOne/${inputOne}`, {
                    method: 'GET',
                    headers: {
                        Authorization: `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });

                if (!response.ok) {
                    throw new Error('Помилка при отриманні даних');
                }

                const postData = await response.json();
                setData(postData);

                // Переходити на нову сторінку з використанням ID
                navigate(`/posts/${inputOne}`);
            } catch (err) {
                setError(err.message);
            }
        } else {
            setError('Токен відсутній');
        }
    };

    return (
        <div className="content">
            <h1>Знайти пост</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Введіть ID посту"
                    value={inputOne}
                    onChange={(event) => setInputOne(event.target.value)}
                />
                <button type="submit">Відправити</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {data && (
                <div>
                    <h2>Дані посту:</h2>
                    <pre>{JSON.stringify(data, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}
