import { useState } from "react";
import axios from "axios";
export default function CreatePost() {

    const [file, setFile] = useState(null);
    const [message, setMessage] = useState('');

    const [email, setEmail] = useState(null)
    const [nickName, setNickName] = useState(null)
    const [password, setPassword] = useState(null)
    const [avatar, setAvatar] = useState(null)

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };
    const handleTitleChange = (e) => {
    };
    const handleTextChange = (e) => {
    };
    const handleTagsChange = (e) => {
    };
    const handleSubmit = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem('token');


        try {
            const response = await fetch('http://localhost:3001/posts/create', {
                method: 'POST',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify({
                    email: email,
                    fullName: nickName,
                    password: password,
                    avatarUrl: avatar,
                })
            });

            const msg = await response.json();
            if (response.ok) {
                setMessage('Файл успішно завантажено');
            } else {
                setMessage(msg.message || 'Щось пішло не так');
            }
        } catch (error) {
            console.error('Помилка при завантаженні файлу:', error);
            setMessage('Сталася помилка при завантаженні файлу');
        }
    };

    return (
        <div className="content">
            <form onSubmit={handleSubmit}>
                <div>
                    <h2>Дані посту:</h2>
                    <div className='post'>
                        <input type="file" onChange={handleFileChange} />
                        <input placeholder="Enter title" type="text" onChange={handleTitleChange}/> 
                        
                        <textarea placeholder="Enter text" onChange={handleTextChange}></textarea>

                        <input placeholder="Enter tags (Use a comma as a separator)" type="text" onChange={handleTagsChange}/> 
                        
                        <input type="submit" onChange={handleSubmit} />

                        <br />
                    </div>
                </div>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}
